mapper.insert:
INSERT /* com.greedy.spring.board.model.dao.BoardMapper#insertBoard() */
  INTO TBL_BOARD A
(
  A.BOARD_NO
, A.BOARD_TYPE
, A.CATEGORY_CODE
, A.BOARD_TITLE
, A.BOARD_BODY
, A.BOARD_WRITER_MEMBER_NO
)
VALUES
(
  SEQ_BOARD_NO.NEXTVAL
, 1
, #{ categoryCode }
, #{ title }
, #{ body }
, #{ writerMemberNo }
)

INSERT /* com.greedy.spring.board.model.dao.BoardMapper#insertReply() */
  INTO TBL_REPLY A
(
  A.REPLY_NO
, A.REF_BOARD_NO
, A.REPLY_BODY
, A.REPLY_WRITER_MEMBER_NO
)
VALUES
(
  SEQ_REPLY_NO.NEXTVAL
, #{ refBoardNo }
, #{ body }
, #{ writerMemberNo }
)

mapper.delete:
DELETE /* com.greedy.spring.board.model.dao.BoardMapper#deleteReply() */
       FROM TBL_REPLY A
 WHERE A.REPLY_NO = #{ replyNo }

mapper.insert:
INSERT /* com.greedy.spring.board.model.dao.BoardMapper#insertAttachment() */
  INTO TBL_ATTACHMENT A
(
  A.ATTACHMENT_NO
, A.REF_BOARD_NO
, A.ORIGINAL_NAME
, A.SAVED_NAME
, A.SAVE_PATH
, A.FILE_TYPE
, A.THUMBNAIL_PATH
)
VALUES 
(
  SEQ_ATTACHMENT_NO.NEXTVAL
, #{ refBoardNo }
, #{ originalName }
, #{ savedName }
, #{ savePath }
, #{ fileType }
, #{ thumbnailPath }
)

mapper.update:
UPDATE /* com.greedy.spring.board.model.dao.BoardMapper#incrementBoardCount() */
       TBL_BOARD A
   SET A.BOARD_COUNT = (SELECT B.BOARD_COUNT
                          FROM TBL_BOARD B
                         WHERE B.BOARD_NO = #{ no }
                       ) + 1
 WHERE A.BOARD_NO = #{ no }
